    /**
     * Called if the MIDlet is terminated by the system.
     * I.e. if startApp throws any exception other than MIDletStateChangeException,
     * if the isolate running the MIDlet is killed with Isolate.exit(), or
     * if VM.stopVM() is called.
     * 
     * It is not called if MIDlet.notifyDestroyed() was called.
     *
     * @param unconditional If true when this method is called, the MIDlet must
     *    cleanup and release all resources. If false the MIDlet may throw
     *    MIDletStateChangeException  to indicate it does not want to be destroyed
     *    at this time.
     */
    protected void destroyApp(boolean unconditional) throws MIDletStateChangeException {
        for (int i = 0; i < 8; i++) {
            leds[i].setOff();
        }
    }

    public void startReceiverThread() {
        new Thread() {

            public void run() {
                String tmp = null;
                RadiogramConnection dgConnection = null;
                Datagram dg = null;

                try {
                    dgConnection = (RadiogramConnection) Connector.open("radiogram://:37");
                    // Then, we ask for a datagram with the maximum size allowed
                    System.out.println("Maximum length: " + dgConnection.getMaximumLength());
                    dg = dgConnection.newDatagram(dgConnection.getMaximumLength());
                } catch (IOException e) {
                    System.out.println("Could not open radiogram receiver connection");
                    e.printStackTrace();
                    return;
                }

                while (true) {
                    try {
                        dg.reset();
                        dgConnection.receive(dg);

                        final String con_addr = dg.getAddress();
                        System.out.println("New connection from: " + con_addr);

                        byte[] bdata = new byte[dg.getLength()];
                        dg.readFully(bdata);

                        int sum=0;
                        for (int i=0; i<bdata.length; i++) {
                            sum = (sum + bdata[i]) % 16777216;
                        }
                        System.out.println("[receiver] len=" + bdata.length + ", sum=" + sum);

//                        hexdump("Incoming data", bdata);


                        final String data = new String(bdata);

                        /*
                         *call function in Lua application
                         *the Lua function will route the message to specified address
                         */
                        new Thread() {
                            public void run() {
                                System.out.println("[dispatch] start");
                                ServiceProvider sp = ServiceProvider.getInstance();
                                sp.dispatch(con_addr, data);
                                System.out.println("[dispatch] finish");
                            }
                        }.start();

//                        System.out.println("Received: " + tmp + " from " + con_addr);
                    } catch (IOException e) {
                        System.out.println("Nothing received");
                    }
                }
            }
        }.start();
    }

